sys_script.script:
(function executeRule(current, previous /*null when async*/) {
		var dt = new GlideDateTime();
		var gr = new GlideRecord('x_58872_needit_needit_state_change');
		gr.initialize();

	//if((current.operation()=='insert') || ((current.operation()=='update') && !current.state.changesTo('6'))) {

		gr.setValue('id', current.number);
		gr.setValue('active', true);
		gr.setValue('modified_on', dt);

// 		gr.id.setValue(current.number); both ways are fine and working
// 		gr.active.setValue(true);
// 		gr.modified_on.setValue(dt);
		//gs.info('Hellllloooo ' + current.number + ' ' + dt);

		gr.insert();

	if(current.state.changesTo('3')) {
		var gr1 = new GlideRecord('x_58872_needit_needit_state_change');
		gr1.addQuery('id', current.number);
		gr1.query();
		gr1.setValue('active', false);
		gr1.updateMultiple();
	}
	// now, get the count of updates made
		var g = new GlideAggregate('x_58872_needit_needit_state_change');
		g.addQuery('id', current.number);
		g.addAggregate('COUNT');
		g.query();
		var i = 0;
		if(g.next()) {
			i = g.getAggregate('COUNT');
		}
		gs.addInfoMessage('Number of updates made to this record: ' + i + '.');

})(current, previous);
